import React from 'react';
import './TestAnswer.css';

const TestAnswer = ({ answer, isSelected, onSetRight }) => {
    const setRightAnswer = () => {
        onSetRight(answer.answer_id);
    };

    const answerClass = isSelected ? 'selected' : '';

    return (
        <label className={`answer_text ${answerClass}`} onClick={setRightAnswer}>
            <input type="radio" name="answer" />
            <span>{answer.answer_text}</span>
        </label>
    );
};

export default TestAnswer;
