import React, {useRef, useState} from 'react';
import "./Queshen.css"
import Answer from "./Answer";

const Queshen = (prop) => {
    const titleInput = useRef()
    const [rightAnswer, setRightAnswer] = useState()

    function getRandomInt(max) {
        return Math.floor(Math.random() * max);
        getRandomInt(1000000000)
    }
    const addAnswer = () => {
        if (prop.item.question_answer.length < 21) {
            prop.item.question_answer.push({
                answer_text: "",
                answer_id:  getRandomInt(1000000000),
                answer_is_right: false
            })
        }
    }
    const deleteAnswer = (id) =>{
        prop.item.question_answer = (prop.item.question_answer.filter((item) => item.answer_id !== id))
    }


    return (
        <div className='queshen'>
            <div className="queshen_index">Question{prop.item.Question_index}</div>
            <div className="queshen_block">
                <input ref={titleInput} onChange={()=> {
                    prop.item.Question_name = titleInput.current.value;
                }} placeholder={prop.item.Question_name} type="text" className="queshen_input"/>
                {prop.item.question_answer.map((item)=>(
                    <Answer onSetRight = { answer => setRightAnswer(answer) }  selectedAnswerId={rightAnswer} key={item.answer_id} answer={item} onDel={() => {
                        deleteAnswer(item.answer_id)
                    } } onSet={ input => item.answer_text = input } />
                ))}
                <button onClick={addAnswer} className="add_option">add an option</button>
            </div>
        </div>
    );
}

export default Queshen;
