import React, {useEffect, useRef, useState} from 'react';
import "./Answer.css"

const Answer = (prop) => {
    const input = useRef()
    const [isSelected, setIsSelected] = useState(false);
    const change = () => {
        prop.onSet(input.current.value)
    }
    const setRightAnswer = () => {
        setIsSelected(true);
        console.log(prop.answer.answer_is_right)
        prop.answer.answer_is_right = true
        console.log(prop.answer.answer_is_right)
        prop.onSetRight(prop.answer.answer_id);
    };
    useEffect(() => {
        setIsSelected(prop.answer.answer_id === prop.selectedAnswerId);
    }, [prop.selectedAnswerId, prop.answer.answer_id]);

    return (
        <div className="ansver">
            <button onClick={setRightAnswer} className={`answer_btn ${isSelected ? 'selected' : ''}`}>✔</button>
            <input ref={input} onChange={change} type="text" placeholder={prop.answer.answer_text} className={`answer_text ${isSelected ? 'selected' : ''}`}/>
            <button onClick={prop.onDel} className="answer_btn delit">✖</button>
        </div>
    );
};

export default Answer;
