import React, {useState} from 'react';
import "./TestAnswer.css"
const TestAnswer = (prop) => {
    const setRightAnswer = () => {
        prop.onSetRight(prop.i.answer_id)
    }

    return (
        <label id = "answer" className="answer_text" onClick={setRightAnswer}>
            <input name = "answer" type="radio" />
            <label htmlFor = "answer" >{prop.i.answer_text}</label>
        </label>
    );
};

export default TestAnswer;
