import React, { useState, useEffect } from 'react';
import './TestAnswer.css';

const TestAnswer = ({ answer, onSetRight, selectedAnswerId }) => {
    const [isSelected, setIsSelected] = useState(false);

    const setRightAnswer = () => {
        setIsSelected(true);
        onSetRight(answer.answer_id);
    };

    useEffect(() => {
        setIsSelected(answer.answer_id === selectedAnswerId);
    }, [selectedAnswerId, answer.answer_id]);

    const handleChange = () => {
        setRightAnswer();
    };

    return (
        <label
            className={`answer_text ${isSelected ? 'selected' : ''}`}
            onClick={setRightAnswer}
        >
            <input
                type="radio"
                name="answer"
                checked={isSelected}
            />
            <span>{answer.answer_text}</span>
        </label>
    );
};

export default TestAnswer;
