import React, {useState} from 'react';
import "./TestAnswer.css"
const TestAnswer = (prop) => {
    const [right, setRight] = useState("answer_text")
    const setRightAnswer = () => {
        prop.onSetRight(prop.i.answer_id)
        if(prop.rightAnswer === prop.i.answer_id){
            setRight("answer_text right")
        }else{
            setRight("answer_text")
        }
    }

    return (
        <div className={right} onClick={setRightAnswer}>
            <input name = "answer" type="radio" id="contactChoice1"/>
            {prop.i.answer_text}
        </div>
    );
};

export default TestAnswer;
