import React, {useState} from 'react';
import TestAnswer from "./TestAnswer";

const TestQuestion = (prop) => {
    const [rightAnswer, setRightAnswer] = useState()
    return (
        <div className='queshen'>
            <div className="queshen_index">Question{prop.e.Question_index}</div>
            <div className="queshen_block">
                {rightAnswer}
                <div>{prop.e.Question_name}</div>
                <div>
                    {prop.e.question_answer.map((i)=>(
                        <div>
                            <input type="radio"/>
                            <TestAnswer name = "answer" rightAnswer = { rightAnswer } i = { i } onSetRight = { (answer)=>setRightAnswer(answer) } />
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default TestQuestion;
