import React, { useState } from 'react';
import { useParams } from "react-router-dom";
import "./Test.css"
import axios from "axios";
import Queshen from "../Queshen";
import TextLink from "../TextLink";
import Answer from "../Answer";
const Test = () => {
    const id = useParams().id
    const [testInfo, setTestInfo] = useState("")
    const [testArr, setTestArr] = useState([])
    const getTestInfo = async (i) =>{
        try {
            const response = await axios.post('http://localhost:5000/getTestInfo', { i });
            setTestInfo(response.data.testInfo);
            setTestArr(response.data.testInfo.userArrowQuestion);
        } catch (error) {
            console.error(error);
        }
    }
    if(testInfo === ""){
        getTestInfo(id)
    }
    return (
        <div className="wrapper">
            {testArr.map((e)=>(
                <div className='queshen'>
                    <div className="queshen_index">Question{e.Question_index}</div>
                    <div className="queshen_block">
                        <div>{e.Question_name}</div>
                        {answerArrow.map((i)=>(
                            <Answer key={i.answer_id} i={i} onDel={() => {
                                deleteAnswer(i.answer_id)
                            } } onSet={(input)=>{i.answer_text = input; prop.item.question_answer = answerArrow}} />
                        ))}
                        <button onClick={addAnswer} className="add_option">add an option</button>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Test;
